<?phpsession_start();$dsn = 'mysql:dbname=mydb;host=localhost';$user = 'root';$password = 'root';try {    // Création d’une nouvelle connexion MySQL via PDO    $dbh = new PDO($dsn, $user, $password);    $dbh->setAttribute(PDO::ATTR_ERRMODE, PDO::ERRMODE_EXCEPTION);} catch (PDOException $e) {    $e->getMessage();}//$dbh = null; // Destruction de la connexion MySQL